sp_widget.client_script:
function($scope) {
  /* widget controller */
  var c = this;

	var input = document.getElementById("barcode");
input.addEventListener("keyup", function(event) {
    event.preventDefault();
    if (event.keyCode === 13) {
        document.getElementById("myBtn").click();
    }
});

	c.reset = function(){
		//window.location.reload(1);
	}

	setTimeout(function(){
   window.location.reload(1);
}, 300000);


		c.submit = function(){
		c.server.update();
		setTimeout(function(){
			switch (c.data.state){
				case '2':
						swal({
					title: 'Account Error',
					html: 'Your account needs to be updated, please see front office staff',
					timer: 2000,
					type: 'warning',
					allowEnterKey: false,
					showCancelButton: false,
					showConfirmButton: false
				});	
						break;

					case '1':
							 swal({
					title: 'Welcome ' + c.data.firstName,
					html: 'Enjoy your workout',
					timer: 2000,
					type: 'success',
					allowEnterKey: false,
					showCancelButton: false,
					showConfirmButton: false
				});
						break;	 

				default:
							swal({
					title: 'Barcode Error',
					html: 'Please verify your barcode or, please see front office staff',
					timer: 2000,
					type: 'error',
					allowEnterKey: false,
					showCancelButton: false,
					showConfirmButton: false
				});
						break;

			}

			c.data.state = '';
			c.data.firstName = '';

},1000);

	}
}

